import React, { useState } from 'react';
import { connect } from 'react-redux';
import { Navigate } from 'react-router-dom';
import { AppBar, Toolbar, Typography, Button, withStyles } from '@material-ui/core';
import AlertBox from '../../atoms/Alertbox/AlertBox';
import LoginForm from '../../templates/loginForm/loginForm';
import Auth from '../../../helper/Auth';

const useStyles = (theme) => ({
  addHeight: theme.mixins.toolbar,
  title: {
    flexGrow: 1
  },
  main: {
    textAlign: 'center',
    paddingTop: '5%',
    margin: 'auto'
  }
});

const LoginPage = ({ classes, user }) => {
  const [gotoStudentRegister, setGotoStudentRegister] = useState(false);

  const onStudentRegisterClick = () => {
    setGotoStudentRegister(true);
  };

  if (gotoStudentRegister) {
    return <Navigate to='/studentRegisterPage' />;
  }

  if (user.isLoggedIn) {
    return <Navigate to={user.userDetails.type === 'TEACHER' ? '/homeTeacher' : '/homeStudent'} />;
  } else if (Auth.retrieveToken() && Auth.retrieveToken() !== 'undefined') {
    return <Navigate to='/homeStudent' />;
  }

  return (
    <div>
      <AppBar elevation={0} className={classes.appbar}>
        <Toolbar>
          <Typography variant='h5' className={classes.title}></Typography>
          <Typography variant='h6'>
            <Button variant="contained" className={classes.endtestbtn} onClick={onStudentRegisterClick}>
              Student Register
            </Button>
          </Typography>
        </Toolbar>
      </AppBar>
      <div className={classes.addHeight}></div>
      <div className={classes.main}>
        <AlertBox />
        <LoginForm />
      </div>
    </div>
  );
};

const mapStateToProps = (state) => ({
  user: state.user
});

export default withStyles(useStyles)(connect(mapStateToProps, {})(LoginPage));
